mapper.select:
SELECT d.device_id AS deviceId, d.imei_code AS deviceImei, CONCAT(o.surname,o.name) AS deviceUserName, d.device_mobile AS devicePhone, GROUP_CONCAT(e.mobile," ") AS familyPhones FROM device d LEFT JOIN old_man o ON d.device_id=o.device_id LEFT JOIN emergency e ON o.old_man_id=e.old_man_id
WHERE d.imei_code =#{deviceImei}
GROUP BY d.device_id

SELECT d.imei_code as deviceIMEI, DATE_FORMAT(l.location_time,'%Y-%m-%d %H:%i:%s') as locationTime, l.location_type as locationType, l.longitude as locationLongitude, l.latitude as locationLatitude
FROM device d,location l WHERE l.device_id=d.device_id and d.imei_code=#{deviceImei}
and l.location_time> #{startTime}
and #{endTime} > l.location_time
ORDER BY l.location_time

SELECT d.imei_code as deviceIMEI, d.device_mobile as deviceMobile, d.power as locationPower, (case d.bluetooth_status when 1 then '蓝牙断开' when 0 then '蓝牙正常连接' end ) as bluetoothStatus FROM device d
WHERE d.imei_code=#{deviceIMEI}
ORDER BY d.online_time DESC

mapper.update:
          UPDATE 
			     device d
			SET  d.bluetooth_status=#{bluetoothStatus}
		  WHERE  d.imei_code=#{deviceIMEI}

mapper.select:
SELECT d.imei_code as deviceIMEI, o.old_man_id as oldManId, o.surname as surname, o.name as name, o.age as age, o.sex as sex, o.address as address, o.mobile as phone, IFNULL(GROUP_CONCAT(e.emergency_id,":",e.mobile),' ') AS familyPhones FROM device d, old_man o LEFT JOIN emergency e ON o.old_man_id=e.old_man_id WHERE d.device_id=o.device_id
AND d.imei_code=#{deviceIMEI}
GROUP BY d.imei_code

mapper.update:
UPDATE 
      emergency e
SET   e.mobile=#{mobile}

WHERE e.emergency_id=#{emergencyId}

mapper.select:
SELECT d.imei_code as deviceIMEI, CONCAT(o.surname,o.`name`) as name, a.alarm_addr as alarmAddress, (CASE a.alarm_type WHEN 2 then '低电报警' WHEN 1 then 'sos报警' WHEN 3 then '蓝牙断开报警' END) as alarm, a.alarm_time, (CASE a.is_call WHEN 1 then '未通知家属救援' WHEN 2 then '已经通知亲人救援' ELSE '无需处理' END) as rescueStatus FROM device d,old_man o,location l,alarm a WHERE d.device_id=o.device_id AND d.device_id=l.device_id AND l.location_id=a.location_id
AND d.imei_code=#{deviceIMEI}
ORDER BY a.alarm_time DESC
